import React, { useState } from 'react'
import { useDispatch, useSelector} from 'react-redux'
import { addTask } from './Redux/counterSlice'

function Todo() {
    const [id, setId] = useState(0)
    const [task, setTask] = useState('')
    const tskob = useSelector( (state) => {
      console.log('STATE IS', state.counter[0].tasks[0].name)
        return state.counter[0].tasks.filter(task => {
          if(task.id === 0){
            console.log("INSIDE IF", task)
            return task
          } 
           else return 'none'  })
    })

  return (
    <div>
      ID IN THE BEGINNING IS {tskob.name}
      Id: <input type='text' onChange={(e) => {
          setId(e.target.value)
      }
      } />
      Task: <input type='text' onChange= {(e) => {
          setTask(e.target.value)
      }} />
    </div>
  )
}

export default Todo
