  return (
    <div>
      ID IN THE BEGINNING IS {}
      Id: <input type='text' onChange={(e) => {
          setId(e.target.value)
      }
      } />
      Task: <input type='text' onChange= {(e) => {
          setTask(e.target.value)
      }} />
    </div>
  )
}

export default Todo
